long long sum[102][102];
long long f[102][102][102];
long long g[102][102][102];
class Solution {
public:
    long long maximumScore(vector<vector<int>>& grid) {
        int n = grid.size();
        memset(f,0,sizeof(f));
        memset(sum,0,sizeof(sum));

        for(int i=0;i<n;++i){
            sum[i][0] = 0;
            for(int j=0;j<n;++j){
                sum[i][j+1] = sum[i][j] + grid[j][i];
            }
        }
        if(n == 1)return 0;
        for(int i=0;i<=n;++i){
            for(int j=0;j<=n;++j){
                if(i >= j) f[1][i][j] = 0;
                else f[1][i][j] = sum[0][j] - sum[0][i]; 
                g[1][i][j] = f[1][i][j];
                if(i) g[1][i][j] = max(g[1][i][j], g[1][i-1][j]);
            }
        }

        for(int ii=1;ii<n-1;++ii){
            for(int i=0;i<=n;++i){
                for(int j=0;j<=n;++j){
                    long long aug = 0;
                    if(i > j) aug = sum[ii][i] - sum[ii][j];
                    f[ii+1][j][i] = g[ii][i][j] + aug;
                    for(int k=i+1;k<=n;++k){
                        int x = max(i,k);
                        aug = 0;
                        if(x > j) aug = sum[ii][x] - sum[ii][j];
                        f[ii+1][j][i] = max(f[ii+1][j][i], f[ii][k][j] + aug);                        
                    }
                    g[ii+1][j][i] = f[ii+1][j][i];        
                    if(j) g[ii+1][j][i] = max(g[ii+1][j][i], g[ii+1][j-1][i]);
                /*    for(int k=0;k<=n;++k){
                        int x = max(i,k);
                        long long aug = 0;
                        if(x > j) aug = sum[ii][x] - sum[ii][j];
                        f[ii+1][j][k] = max(f[ii+1][j][k], f[ii][i][j] + aug);
                    }*/
                }
            }


        }
        long long ans = 0;
        for(int i=0;i<=n;++i)for(int j=0;j<=n;++j){
            int x = i;
            long long aug = 0;
            if(x > j) aug = sum[n-1][x] - sum[n-1][j];            
            ans = max(ans, f[n-1][i][j] + aug);
        }
        return ans;
    }
};
