class Solution {
public:
    long long maximumScore(vector<vector<int>>& g) {
        int n = siz(g);
        vvll psa(n+2, vll(n+2, 0));
        for (int j = 0; j < n; j++){
            for (int i = 0; i < n; i++){
                psa[j][i+1] = psa[j][i]+g[i][j];
            }
        }
        vector<vvll> dp(n+2, vvll(n+2, vll(n+2, -LLINF)));
        vll best(n+2, 0);
        vvll poo(n+2, vll(n+2, -LLINF));
        for (int i = 0; i <= n; i++){
            dp[0][0][i] = 0;
            for (int j = 0; j <= n; j++){
                poo[i][j] = -psa[0][i];
            }
        }
        ll mx = 0;
        for (int i = 1; i < n; i++){
            for (int j = 0; j <= n; j++){
                for (int k = 0; k <= n; k++){
                    if (k < j){
                        dp[i][j][k] = best[j]+psa[i][j]-psa[i][k];
                    } else {
                        dp[i][j][k] = max(best[j], poo[j][k]+psa[i-1][k]);
                    }
                }
            }
            for (int j = 0; j <= n; j++){
                best[j] = 0;
                for (int k = 0; k <= n; k++) poo[i][j] = -LLINF;
            }
            for (int j = 0; j <= n; j++){
                for (int k = 0; k <= n; k++){
                    best[k] = max(best[k], dp[i][j][k]);
                    poo[k][j] = max((j? poo[k][j-1] : -LLINF),
                                dp[i][j][k]-psa[i][max(k, j)]);
                    if (i == n-1){
                        mx = max(mx, dp[i][j][k]);
                    }
                    //deb(i, j, k, dp[i][j][k]);
                }
            }
        }
        return mx;
    }
};
