long long dp[105][105];
long long dp2[105][105];
class Solution {
public:
    long long maximumScore(vector<vector<int>>& a) {
        int i,j,k;
        int n = a.size();
        for (i=0;i<=n;++i) {
            for (j=0;j<=n;++j) {
                dp[i][j] = -1e18;
                dp2[i][j] = -1e18;
            }
        }
        dp[0][0] = 0;
        for (i=0;i<n;++i) {
            //k = pre;
            // from left

            //dp1 -> dp1
            for (k=0;k<=n;++k) {
                long long add = 0;
                for (j=0;j<k;++j) {
                    add += a[j][i];
                }
                for (j=0;j<=n;++j) {
                    dp[i+1][j] = max(dp[i+1][j], dp[i][k] + add);
                    if (j < k && j != n) {
                        add -= a[j][i];
                    }
                }
            }
            //dp1 -> dp2
            long long pre_max = 0;
            for (k=0;k<=n;++k) {
                pre_max = max(pre_max, dp[i][k]);
            }
            long long add = 0;
            for (j=0;j<=n;++j) {
                dp2[i+2][j] = max(dp2[i+2][j], pre_max + add);
                if (j != n) {
                    add += a[j][i];
                }
            }
            //dp2->dp2
            for (k=0;k<=n;++k) {
                add = 0;
                for (j=0;j<=n;++j) {
                    dp2[i+2][j] = max(dp2[i+2][j], dp2[i+1][k] + add);
                    if (j >= k && j != n) {
                        add += a[j][i];
                    }
                }
            }
            //dp2->dp1
            for (k=0;k<=n;++k) {
                dp[i+1][k] = max(dp[i+1][k], dp2[i+1][k]);
            }

        }
        /*
        printf("dp1\n");
        for (i=0;i<=n;++i) {
            for(j=0;j<=n;++j) {
                printf("%lld ", dp[i][j]);
            }
            puts("");
        }
        printf("dp2\n");
        for (i=0;i<=n;++i) {
            for(j=0;j<=n;++j) {
                printf("%lld ", dp2[i][j]);
            }
            puts("");
        }
        */
        long long ans = 0;
        for (i=0;i<=n;++i) {
            for (j=0;j<=n;++j) {
                ans = max(ans, dp[i][j]);
            }
        }
        return ans;
    }
};
