using LL = long long;
template<typename T>
void chkMax(T& a, T b)
{
    if(a<b)a = b;
}
LL f[105][2], h[105][2], a[105][105];

class Solution {
public:
	LL get(int i, int x, int y) {
		if (y >= x) return a[y][i] - a[x][i];
		return 0;
	}
    long long maximumScore(vector<vector<int>>& g) {
     	int n = g.size();
     	for (int i = 0; i < n; i++) {
     		for (int j = 0; j < n; j++) {
     			a[i + 1][j + 1] = a[i][j + 1] + g[i][j];
     		} 
     	}   
   		memset(f, 0xcf, sizeof f);
   		for (int i = 0; i <= n; i++) f[i][0] = 0;
   		LL ans = 0;
   		for (int i = 1; i < n; i++) {
   			memcpy(h, f, sizeof f);
   			memset(f, 0xcf, sizeof f);
   			for (int x = 0; x <= n; x++) {
   				for (int y = 0; y <= n; y++) {

   					for (int p = 0; p < 2; p++) {
   						for (int q = 0; q < 2; q++) {
   							LL w = h[x][p];
   							if (!p) w += get(i, x, y);
   							if (q) w += get(i + 1, y, x);
   							chkMax(f[y][q], w);
   						}
   					}
   				}
   			}
   		}

   		for (int x = 0; x <= n; x++) {
   			for (int p = 0; p < 2; p++) {
   				chkMax(ans, f[x][p]);
   			}
   		}
   		return ans;
    }

};
