#include <bits/stdc++.h>
using namespace std;
#define rep(i, a, b) for(int i = a; i < (b); ++i)
#define all(x) begin(x), end(x)
#define sz(x) (int)(x).size()
#define PB push_back
#define FS first
#define SD second
#define cmx(x,y) x = max(x,y)
#define cmn(x,y) x = min(x,y)
#define ary(k) array<int,k>
typedef pair<int, int> pii;
typedef vector<int> vi;
typedef long long ll;
const int MM = 105;
ll dp[MM][MM][MM]; // largest placed, largest covered
class Solution {
public:
    long long maximumScore(vector<vector<int>>& grid) {
        int n = grid.size(),m = grid[0].size();
        for(int i = 0; i <= m; i++) for(int j = 0; j <= n; j++) for(int k = j; k <= n; k++) dp[i][j][k] = -1e18;
        vector<vector<ll>> sum(m+1,vector<ll>(n+1));
        for(int i = 1; i <= m; i++){
            for(int j = 1; j <= n; j++){
                sum[i][j] = sum[i][j-1] + grid[j-1][i-1];
            }
        }
        dp[0][0][0] = 0;
        for(int i = 1; i <= m; i++){
            for(int l = 0; l <= n; l++){
                for(int j = 0; j <= n; j++){
                    int p = max(l,j);
                    ll cur = 0;
                    for(int k = j; k <= n; k++){
                        ll add = 0;
                        if(l <= j){
                            add += sum[i][j]-sum[i][l];
                        } else if(l > k){
                            add += sum[i-1][l]-sum[i-1][k];
                        }
                        cmx(cur,dp[i-1][j][k]+add);
                    }
                    cmx(dp[i][l][p],cur);
                }
            }
        }
        //rep(i,0,n+1) rep(j,i,n+1) cout << dp[1][i][j] << " " << i << " " << j << " o\n";
        ll ans = 0;
        rep(i,0,n+1) rep(j,i,n+1) cmx(ans,dp[m][i][j]);
        return ans;
    }
};
