class Solution:
    def maximumScore(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        if m == 1:
            return 0
        f = [[[0 for _ in range(n + 1)] for _ in range(n + 1)] for _ in range(m)]
        pre = [0] * (n + 1)
        for i in range(n):
            pre[i+1] = pre[i] + grid[i][m-1]
        for i in range(n + 1):
            #for j in range(i + 1):
            #    f[m-1][i][j] = 0
            for j in range(i+1, n+1):
                f[m-1][i][j] = pre[j] - pre[i]
        for col in range(m-2, 0, -1):
            pre = [0] * (n + 1)
            for i in range(n):
                pre[i+1] = pre[i] + grid[i][col]
            for i in range(n + 1):
                maxi = [0] * (n + 1)
                best = 0
                for j in range(n + 1):
                    best = max(best, f[col+1][j][i])
                    maxi[j] = best
                best = 0
                for j in range(n, -1, -1):
                    if j > i:
                        f[col][i][j] = max(best, pre[j] - pre[i] + maxi[j])
                        best = max(best, f[col+1][j][i] + pre[j] - pre[i])
                    else:
                        f[col][i][j] = max(best, maxi[i])
        res = 0
        pre = [0] * (n + 1)
        for i in range(n):
            pre[i+1] = pre[i] + grid[i][0]
        for i in range(n + 1):
            for j in range(n + 1):
                val = f[1][i][j] + pre[i] - pre[j] if j < i else f[1][i][j]
                res = max(res, val)
        return res
