func maximumScore(grid [][]int) int64 {

	n := len(grid)
	dp := make([][][]int, n+2)
	for i := range dp {
		dp[i] = make([][]int, n+2)
		for j := range dp[i] {
			dp[i][j] = make([]int, 2)
			for k := range dp[i][j] {
				dp[i][j][k] = -1
			}
		}
	}

	s := make([][]int, n+2)
	for i := range s {
		s[i] = make([]int, n+2)
	}
	for i, row := range grid {
		for j, val := range row {
			s[i+1][j] = s[i][j] + val
		}
	}

	var dfs func(i, j, isDown int) int
	dfs = func(i, j, isDown int) int {
		if j >= n {
			return 0
		}
		if dp[i][j][isDown] >= 0 {
			return dp[i][j][isDown]
		}
		res := 0
		maxJ := n
		if isDown > 0 {
			maxJ = n - 1
		}
		for jj := j; jj < maxJ; jj++ {
			if isDown > 0 {
				if i+1 == n {
					res = max(res, dfs(i, jj+2, 0)+grid[i][jj])
				} else {
					res = max(res, dfs(i+1, jj, 1)+grid[i][jj])
				}
			} else {
				if i-1 < 0 {
					res = max(res, dfs(i, jj+1, 1)+grid[i][jj])
					res = max(res, dfs(n-1, jj+2, 0)+grid[i][jj])
				} else {
					res = max(res, dfs(i-1, jj, 0)+grid[i][jj])
				}
			}
		}
		dp[i][j][isDown] = res
		return res
	}

	ans := max(dfs(0, 0, 1), dfs(n-1, 1, 0))
	return int64(ans)

}
