class Solution {
public:
    long long maximumScore(vector<vector<int>>& grid) {
        int n=grid.size();
        long long dp[n][n+1][2];
        for(int i=0;i<n;++i)
            for(int j=0;j<=n;++j)
            for(int k=0;k<=1;++k)
            dp[i][j][k]=-1e18;

        vector<vector<long long>>gg=vector<vector<long long>>(n,vector<long long>(n));
        for(int i=0;i<n;++i)
            for(int j=0;j<n;++j) gg[i][j]=grid[i][j];
        for(int i=1;i<n;++i)
            for(int j=0;j<n;++j)
            gg[i][j]+=gg[i-1][j];

        for(int j=0;j<=n;++j)
            dp[0][j][0]=dp[0][j][1]=0;

        long long ans=0;
        for(int i=0;i<n;++i)
            for(int j=0;j<=n;++j)
            for(int k=0;k<=1;++k){
                ans=max(ans,dp[i][j][k]);
                if(i+1<n){
                    for(int h=0;h<=n;++h)
                        for(int p=0;p<=1;++p){
                                long long nx=dp[i][j][k];
                                if(h&&k==0&&h>j) nx+=gg[h-1][i]-(j==0?0:gg[j-1][i]);
                                if(j&&p==1&&j>h){
                                    nx+=gg[j-1][i+1]-(h==0?0:gg[h-1][i+1]);
                                }
                                dp[i+1][h][p]=max(dp[i+1][h][p],nx);
                        }
                }
            }
            return ans;
    }
};
