class Solution {
public:
    long long maximumScore(vector<vector<int>>& a) {
        #define ll long long
        int n=a[0].size();
        ll f[101][101];
        ll g[101][101];
        ll sf[101];
        ll sg[101];
        memset(f,-1,sizeof(f));
        memset(g,-1,sizeof(g));
        memset(sf,0,sizeof(sf));
        memset(sg,0,sizeof(sg));
        for(int i=1;i<=n;++i)
            sf[i]=sf[i-1]+a[i-1][0];
        for(int i=0;i<=n;++i)
            f[i][i]=0;
        for(int i=1;i<n;++i){
            for(int j=1;j<=n;++j)
                sg[j]=sg[j-1]+a[j-1][i];
            for(int j=0;j<=n;++j)
                for(int k=0;k<=n;++k)
                    g[i][j]=-1;
            for(int j=0;j<=n;++j)
                for(int k=j;k<=n;++k){
                    ll tmp=f[j][k];
                    if(tmp<0)
                        break;
                    for(int w=0;w<=n;++w){
                        if(w<=j){
                            g[w][j]=max(g[w][j],tmp+sg[j]-sg[w]);
                        }else{
                            g[w][w]=max(g[w][w],tmp+max(0LL,sf[w]-sf[k]));
                        }
                    }
                }
            swap(f,g);
            swap(sf,sg);
        }
        ll ans=0;
        for(int i=0;i<=n;++i)
            for(int j=i;j<=n;++j)
                ans=max(ans,f[i][j]);
        return ans;
    }
};
