class Solution {
public:
    long long maximumScore(vector<vector<int>>& grid) {
        // grid=vector<vector<int>>(100,vector<int>(100));
        int n=grid.size();
        // cout<<"n="<<n<<endl;
        vector<long long> a(n*n+2*n+5,10000ll*INT_MIN);
        // a[0]=0;
        for(int i=0;i<=n;++i){
            a[i]=0;
        }
        vector<vector<long long>> c;
        for(int j=0;j<n;++j){
            long long s=0;
            c.push_back({0});
            for(int i=0;i<n;++i){
                c.back().push_back(s+=grid[i][j]);
            }
        }
        for(int j=1;j<n;++j){
            vector<long long> b(n*n+2*n+5,10000ll*INT_MIN);
            // vector<long long> c{0};
            // long long s=0;
            // for(int i=0;i<n;++i){
            //     c.push_back(s+=grid[i][j]);
            // }
            for(int i=0;i<=n;++i){
                long long M=10000ll*INT_MIN;
                long long bM=10000ll*INT_MIN;
                long long t=10000ll*INT_MIN;
                // vector<long long> bM(n+2,10000ll*INT_MIN);
                for(int l=0;l<=n;++l){
                    M=max(M,a[l*(n+1)+i]);
                    if(l==i){
                        t=M;
                    }
                    // bM[n-l]=max(bM[n+1-l],a[(n-l)*(n+1)+i]);
                }
                for(int l=n;l>=i;--l){
                    bM=max(bM,a[l*(n+1)+i]);
                    b[i*(n+1)+l]=max(b[i*(n+1)+l],bM);
                }
                long long z=t;
                for(int k=0;k<=n;++k){
                    if(k<=i){
                        b[i*(n+1)+k]=max(b[i*(n+1)+k],M+c[j][i]-c[j][k]);
                    }
                    else{
                        b[i*(n+1)+k]=max(b[i*(n+1)+k],t+c[j-1][k]-c[j-1][i]);
                        // b[i*(n+1)+k]=max(b[i*(n+1)+k],bM[k]);
                        z+=grid[k-1][j-1];
                        z=max(z,a[k*(n+1)+i]);
                        b[i*(n+1)+k]=max(b[i*(n+1)+k],z);
                    }

                }
            }
            swap(a,b);
        }

        return *max_element(a.begin(),a.end());
    }
};
