class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n=grid.size(),m=grid[0].size();
        map<pair<int,int>,int> ma[2];
        for(int i = 0;i<grid.size();i++){
            for(int j = 0;j<grid[0].size();j++){
                int x=min(i,n-i-1),y=min(j,m-j-1);
                ma[grid[i][j]][make_pair(x,y)]++;
            }
        }
        int a=0,b=0,c=0;
        for(int i = 0;i<grid.size();i++){
            for(int j = 0;j<grid[0].size();j++){
                if(i<=n-i-1&&j<=m-j-1){
                    int z=ma[0][make_pair(i,j)],one=ma[1][make_pair(i,j)];
                    //printf("%d %d\n",z,one);
                    if(z+one==4){
                        ans+=min(z,one);
                    }
                    else if(z+one==2){
                        if(z==2)a++;
                        else if(z==1)b++;
                        else if(z==0)c++;
                    }
                    else{
                        ans+=one;
                    }
                }
            }
        }
        if(c%2==0)ans+=b;
        else if(b)ans+=b;
        else ans+=2;
        return ans;
    }
};
