class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int m=g.size();
        int n=g[0].size();
        int res=0;
        for(int i=0;i<m/2;++i){
            for(int j=0;j<n/2;++j){
                int t=g[i][j]+g[m-1-i][j]+g[i][n-1-j]+g[m-1-i][n-1-j];
                res+=min(t,4-t);
            }
        }
        int t=0;
        int a=0;
        if(n&1){
            for(int i=0;i<m/2;++i){
                if(g[i][n/2]!=g[m-1-i][n/2]){
                    ++t;
                }
                else if(g[i][n/2]){
                    a+=2;
                }
            }
        }
        if(m&1){
            if(n&1){
                res+=g[m/2][n/2];
            }
            for(int j=0;j<n/2;++j){
                if(g[m/2][j]!=g[m/2][n-1-j]){
                    ++t;
                }
                else if(g[m/2][j]){
                    a+=2;
                }
            }

        }
        // cout<<"a="<<a<<" t="<<t<<endl;
        if(a%4>0&&t==0){
            res+=2;
        }
        else{
            res+=t;
        }
        return res;
    }
};
