import javax.xml.transform.Source;

class Solution {
    public int minFlips(int[][] g) {
        int n = g.length, m = g[0].length;
        int res = 0;
        int one = 10000000;
        for (int i = 0; i < n / 2; i++){
            for (int j = 0; j < m / 2; j++){
                int a = g[i][j], b = g[n - 1 - i][j], c = g[i][m - 1 - j], d = g[n - 1 - i][m - 1 - j];
                int[] ab = new int[2];
                ab[a]++;
                ab[b]++;
                ab[c]++;
                ab[d]++;
                res += Math.min(ab[0], ab[1]);
                one = Math.min(one, ab[1]);
            }
        }
        int p = 0;
        int q = 0;
        if (n % 2 != 0) {
            for (int j = 0; j < m / 2; j++) {
                if (g[n / 2][j] != g[n / 2][m - 1 - j]) {
                    p++;
                } else {
                    if (g[n / 2][j] == 1){
                        q++;
                    }
                }
            }
        }
        if (m % 2 != 0) {
            for (int j = 0; j < n / 2; j++) {
                if (g[j][m / 2] != g[n - 1 - j][m / 2]) {
                    p++;
                }
                else {
                    if (g[j][m / 2] == 1){
                        q++;
                    }
                }
            }
        }
        if (n % 2 != 0 && m % 2 != 0){
            if (g[n / 2][m / 2] == 1){
                res++;
            }
        }
        if (p == 0){
            if (q % 2 == 0){
                return res + p;
            } else {
                return res + 2;
            }
        }
        if (p == 1){
            return res + p;
        }
        return res + p;

    }
}
