class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int cntr =0;
        for(int i=0;i<m/2;i++){

           for(int j =0;j<n/2;j++)
           {
               int d = grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-i-1][n-j-1];
              cntr+=min(d,4-d);
           }
        }
        if(m&1 && n&1)
        {
            cntr+=grid[m/2][n/2];
        }
        int cnt1 =0,cntr1=0,cntr2=0;
        if(m&1)
        {   int k = m/2;
            int i =0 , j=n-1;
        while(i<j)
        {
           if(grid[k][i]!=grid[k][j])
           {
            cntr1++;
           }else{
                 cnt1+=(grid[k][i]+grid[k][j]);
           }
           i++;
           j--;

        }
        }

        if(n&1)
        {
         int k = n/2;
         int i =0 , j=m-1;
        while(i<j)
        {
           if(grid[i][k]!=grid[j][k])
           {
            cntr1++;
           }else{
             cnt1+=(grid[i][k]+grid[j][k]);
           }
           i++;
           j--;

        }
        }
        // cout<<cnt1<<endl;
        int d = cnt1%4;
        if(cntr1>0)
        {
            return cntr1+cntr;
        }

        return cntr+cntr1+min(d,4-d);
    }
};
