class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size();
        int m = g[0].size();
        vector<vector<int>>vis(n+1,vector<int>(m+1,0));
        int ans = 0,xx = 0,oneone = 0,zerozero = 0;
        for(int i = 0;i < n;i++){
            for(int j = 0;j < m;j++){
                if(!vis[i][j]){
                    int x = n-1-i;
                    int y = m-1-j;
                    vis[i][j] = 1;
                    vis[i][y] = 1;
                    vis[x][j] = 1;
                    vis[x][y] = 1;
                    if(x != i && y != j){
                        int cc = (g[i][j] + g[i][y]+g[x][j]+g[x][y])%4;
                        if(cc == 3) cc = 1;
                        ans += cc;
                        continue;
                    }
                    if(x == i && y == j){
                        if(g[i][j] == 1) ans++;
                        continue;
                    }
                    if(x == i){
                        if(g[i][j] != g[i][y]){
                            xx++;
                        }else if(g[i][j] == 1){
                            oneone++;
                        }else{
                            zerozero++;
                        }
                    }
                    if(y == j){
                        if(g[i][j] != g[x][j]){
                            xx++;
                        }else if(g[i][j] == 1){
                            oneone++;
                        }else{
                            zerozero++;
                        }
                    }
                }
            }
        }
        //cout<<oneone<<" "<<zerozero<<" "<<xx<<endl;
        if((oneone*2)%4 == 0){
            ans += xx;
            return ans;
        }else{
            if(xx > 0) {
                ans += xx;
                return ans;
            }else{
                ans += 2;
                return ans;
            }
        }
    }
};
