class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;
        if((m & n & 1) && grid[m / 2][n / 2]){
            ans++;
            grid[m / 2][n / 2] = 0;
        }

        int row = 0, col = 0;
        int cnt = 0;
        if(m & 1){
            for(int i = 0; i < n / 2; ++i){
                cnt += grid[m / 2][i] + grid[m / 2][n - i - 1];
                if(grid[m / 2][i] != grid[m / 2][n - i - 1]) row++;
            }
        }
        if(n & 1){
            for(int i = 0; i < m / 2; ++i){
                cnt += grid[i][n / 2] + grid[m - i - 1][n / 2];
                if(grid[i][n / 2] != grid[m - i - 1][n / 2]) col++;
            }
        }

        // 1' xd + 2
        // 2' xd + 4
        // cnt -= row + col;
        ans += row + col;
        if(((cnt - row - col) % 4) == 2 && row + col <= 0) ans += 2;

        for(int i = 0; i < m / 2; ++i){
            for(int j = 0; j < n / 2; ++j){
                int count = grid[i][j] + grid[i][n - j - 1]
                            + grid[m - i - 1][j] + grid[m - i - 1][n - j - 1];

                ans += min(4 - count, count);
            }
        }
        return ans;
    }
};
