class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n=v.size(), m=v[0].size(), res=0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                vector<int> s(2);
                s[v[i][j]]++;
                s[v[i][m-j-1]]++;
                s[v[n-i-1][j]]++;
                s[v[n-i-1][m-j-1]]++;
                int op1=s[0], op2=s[1];
                res+=4-max(op1, op2);
            }
        }
        if(n%2 && m%2){
            if(v[n/2][m/2])res++, v[n/2][m/2]=0;
            // int l=0, r=m-1, req=0;
            // while(l<=r){
            //     if(v[n/2][l]!=v[n/2][r]){
            //         req++, res++;
            //         v[n/2][l]=1;
            //         v[n/2][r]=1;
            //     }
            //     l++, r--;
            // }
            // l=0, r=n-1;
            // while(l<=r){
            //     if(v[l][m/2]!=v[r][m/2]){
            //         req++, res++;
            //         v[l][m/2]=1;
            //         v[r][m/2]=1;
            //     }
            //     l++, r--;
            // }
            // int op1=0;
            // for(int j=0; j<m; j++){
            //     op1+=v[n/2][j];
            // }
            // for(int i=0; i<n; i++){
            //     if(i==n/2)continue;
            //     op1+=v[i][m/2];
            // }
            // if(op1%4==2){
            //     if(req)req--;
            //     if(req)req--;
            //     op1-=2;
            // }
            // int op2=n+m-1-op1;

            int l =0, r=m-1, op1=0, cnt=0;
            while(l<=r){
                if(v[n/2][l]==v[n/2][r] && v[n/2][l]){
                    op1++;
                }
                l++,r--;
            }
            l=0, r=n-1;
            while(l<=r){
                if(v[l][m/2]==v[r][m/2] && v[l][m/2]){
                    op1++;
                }
                l++, r--;
            }
            l=0, r=m-1;
            while(l<=r){
                if(v[n/2][l]!=v[n/2][r]){
                    cnt++;
                }
                l++,r--;
            }
            l=0, r=n-1;
            while(l<=r){
                if(v[l][m/2]!=v[r][m/2])cnt++;
                l++, r--;
            }
            res+=cnt;
            int tot_1=op1*2+cnt*2;
            if(tot_1%4==2){
                if(cnt)cnt--, tot_1-=2;
                else op1--, tot_1-=2, res+=2;
            }
        }
        else if(n%2){
            int l =0, r=m-1, op1=0;
            while(l<=r){
                if(v[n/2][l]==v[n/2][r] && v[n/2][l]){
                    op1++;
                }
                l++,r--;
            }
            int cnt=0;
            l=0, r=m-1;
            while(l<=r){
                if(v[n/2][l]!=v[n/2][r]){
                    cnt++;
                }
                l++,r--;
            }
            res+=cnt;
            int tot_1=op1*2+cnt*2;
            if(tot_1%4==2){
                if(cnt)cnt--, tot_1-=2;
                else op1--, tot_1-=2, res+=2;
            }
        }
        else if(m%2){
            int l=0, r=n-1, op1=0;
            while(l<=r){
                if(v[l][m/2]==v[r][m/2] && v[l][m/2]){
                    op1++;
                }
                l++, r--;
            }
            int cnt=0;
            l=0, r=n-1;
            while(l<=r){
                if(v[l][m/2]!=v[r][m/2])cnt++;
                l++, r--;
            }
            res+=cnt;
            int tot_1=op1*2+cnt*2;
            if(tot_1%4==2){
                if(cnt)cnt--, tot_1-=2;
                else op1--, tot_1-=2, res+=2;
            }
        }

        return res;
    }
};
