class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int a=0,b=0,c=0,d=0;
        int count=0,e=0,f=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                e=0,f=0;
                a=grid[i][j]==0?e++:f++;
                b=grid[(n-1)-i][j]==0?e++:f++;
                c=grid[i][(m-1)-j]==0?e++:f++;
                d=grid[(n-1)-i][(m-1)-j]==0?e++:f++;
                count=count+min(4-e,(4-f));

            }
        }
        e=0,f=0,a=0;

        if(n%2!=0)
        {
            c=0,d=m-1;
            b=(n/2);
            while(d>c)
            {
                if(grid[b][c]!=grid[b][d])
                {
                    a++;
                }
                else if(grid[b][c]==1)
                {
                    e++;
                }
                c++;
                d--;
            }
        }

        if(m%2!=0)
        {
            c=0,d=n-1;

            b=(m/2);

            while(d>c)
            {
                if(grid[c][b]!=grid[d][b])
                {
                    a++;
                }
                else if(grid[c][b]==1)
                {
                    e++;
                }
                c++;
                d--;
            }
        }

        int z=e*2;
        if(n%2!=0&&m%2!=0)
        {
            if(grid[(n/2)][(m/2)]==1)
            {
                count++;
            }
        }
        if(z%4==0)
        {
        return count+a;
        }
        else if(z%4!=0&&a>0)
        {
            return count+a;
        }
        else
        {
            return count+2;
        }


    }
};
