class Solution {
public:
    int equal(int a, int b, int c, int d){
        int c0 = 0;
        int c1 = 0;

        if(a == 0) c0++;
        else c1++;

        if(b == 0) c0++;
        else c1++;

        if(c == 0) c0++;
        else c1++;

        if(d == 0) c0++;
        else c1++;

        return max(c0, c1);
    }

    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int cnt = 0;
        for(int j=0; j<m/2; j++){
            for(int i=0; i<n/2; i++){
                int v1 = grid[i][j];
                int v2 = grid[i][m-1-j];
                int v3 = grid[n-1-i][j];
                int v4 = grid[n-1-i][m-1-j];

                cnt += 4 - equal(v1, v2, v3, v4);
            }
        }

        int eq = 0;
        int un = 0;
        if(n % 2 == 1){
            for(int j=0; j<m/2; j++){
                if(grid[n/2][j] != grid[n/2][m-1-j]){
                    cnt++;
                    un++;
                }else{
                    if(grid[n/2][j] == 1) eq++;
                }
            }
        }

        if(m % 2 == 1){
            for(int i=0; i<n/2; i++){
                if(grid[i][m/2] != grid[n-1-i][m/2]){
                    cnt++;
                    un++;
                }else{
                    if(grid[i][m/2] == 1) eq++;
                }
            }
        }

        if(n%2 == 1 && m%2 == 1){
            if(grid[n/2][m/2] == 1){
                cnt++;
            }
        }

        eq *= 2;

        int rem = eq % 4;
        if(rem != 0){
            if(un == 0){
                cnt += 2;
            }
        }

        return cnt;

    }
};
