class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int u=0,d=m-1;
        int cnt=0,loc=0,num=0;
        while(u<d){
            int l=0,r=n-1;
            while(l<r){
                int sum=grid[u][l]+grid[u][r]+grid[d][l]+grid[d][r];
                cnt+=min(sum,4-sum);
                l++;
                r--;
            }
            if(l==r&&grid[u][l]!=grid[d][l]){
                loc++;
                cnt++;
            }
            if(l==r&&grid[u][l]==1&&grid[d][l]==1) num+=2;
            u++;
            d--;
        }
        if(u==d){
            if(n%2==1&&grid[u][n/2]==1) cnt++;
            int left=0,right=n-1;
            while(left<right){
                if(grid[u][left]!=grid[u][right]){
                    loc++;
                    cnt++;
                }
                else{
                    if(grid[u][left]) num+=2;
                }
                left++;
                right--;
            }
        }
        num=num%4;
        num/=2;
        if(loc>=num) return cnt;
        else return cnt+2;
    }
};
