class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int r=g.size(),c=g[0].size();
        int ans=0;
        for(int i=0;i<r/2;i++){
            for(int j=0;j<c/2;j++){
                int ones=g[i][j]+g[i][c-j-1]+g[r-i-1][j]+g[r-i-1][c-j-1];
                ans+=min(ones,4-ones);
            }
        }
        int ones=0,flips=0;
        if(r%2==1){
            for(int i=0;i<c/2;i++){
                if(g[r/2][i]!=g[r/2][c-i-1]){
                    flips++;
                }
                else if(g[r/2][i]==1) ones+=2;
            }
        }
        if(c%2==1){
            for(int i=0;i<r/2;i++){
                if(g[i][c/2]!=g[r-i-1][c/2]) flips++;
                else if(g[i][c/2]==1) ones+=2;
            }
        }
        ans += (r%2==1 && c%2==1 && g[r/2][c/2]==1);
        return ans+flips+((ones%4==2 && flips==0) ? 2:0 );
    }
};
