class Solution {
    public int minFlips(int[][] a) {
        int n = a.length, m = a[0].length;
        int one =0,ans=0;
        for(int i = 0;i<n/2;i++){
            for(int j = 0;j<m/2;j++){
                int sum = a[i][j]+a[n-i-1][j]+a[i][m-1-j] + a[n-i-1][m-1-j];
                if(sum==1){
                    ans++;
                }else if(sum==2)ans+=2;
                else if(sum==3)ans++;
            }
        }
        int cnt=0;
        if(n%2==1){
            int idx = n/2;
            for(int j=0;j<m/2;j++){
                if(a[idx][j]!=a[idx][m-1-j]){
                    ans++;
                    one++;
                    cnt++;
                }else if(a[idx][j]==1)one+=2;
            }
        }
        if(m%2==1){
            int idx = m/2;
            for(int i =0;i<n/2;i++){
                if(a[i][idx]!=a[n-i-1][idx]){
                    ans++;
                    cnt++;
                    one++;
                }else if(a[i][idx]==1)one+=2;
            }
        }
        if(n%2==1 && m%2==1 && a[n/2][m/2]==1)ans++;
        one%=4;
        if(cnt==0 && one==2)ans+=2;
        return ans;
    }
}
