class Solution {
public:

    int ftt(int iidd, int mysum, vector<vector<int>>& vec_pair,
               vector<vector<int>>& memoo) {
        if (iidd >= vec_pair.size()) {
            if (mysum == 0) {
                return 0;
            }
            return 1e9;
        }

        if (memoo[iidd][mysum] != -1) {
            return memoo[iidd][mysum];
        }

        int full = 1e9;
        int ts = mysum;
        ts = (ts + vec_pair[iidd][0] + vec_pair[iidd][1]) % 4;
        full = min(full,
                  vec_pair[iidd][0] + ftt(iidd + 1, ts, vec_pair, memoo));
        full = min(full, vec_pair[iidd][1] +
                           ftt(iidd + 1, mysum, vec_pair, memoo));
        return memoo[iidd][mysum] = full;
    }


    int minFlips(vector<vector<int>>& matrix) {
        vector<vector<int>> vec_pair;
        int n = matrix.size();
        int m = matrix[0].size();

        int i = 0, j = n - 1;
        while (i < j) {
            int a = 0, b = m - 1;
            while (a < b) {
                vector<int> pnntt(2, 0);
                pnntt[matrix[i][a]]++;
                pnntt[matrix[i][b]]++;
                pnntt[matrix[j][a]]++;
                pnntt[matrix[j][b]]++;
                vec_pair.push_back(pnntt);
                a++;
                b--;
            }

            if (a == b) {
                vector<int> pnntt(2, 0);
                pnntt[matrix[i][a]]++;
                pnntt[matrix[j][b]]++;
                vec_pair.push_back(pnntt);
            }
            i++;
            j--;
        }

        if (i == j) {
            int a = 0, b = m - 1;
            while (a < b) {
                vector<int> pnntt(2, 0);
                pnntt[matrix[i][a]]++;
                pnntt[matrix[i][b]]++;
                vec_pair.push_back(pnntt);
                a++;
                b--;
            }
            if (a == b) {
                vector<int> pnntt(2, 0);
                pnntt[matrix[i][a]]++;
                vec_pair.push_back(pnntt);
            }
        }

        int sz = vec_pair.size();
        vector<vector<int>> memoo(sz + 1, vector<int>(4, -1));
        int full = ftt(0, 0, vec_pair, memoo);
        return full;
    }
};
