class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();


        int reqflip=0;
        for(int i=0;i<(n/2);i++)
        {
            for(int j=0;j<(m/2);j++)
            {
                int oc=0;
                if(grid[i][j]==1)
                oc++;

                if(grid[n-1-i][j]==1)
                oc++;

                if(grid[i][m-1-j]==1)
                oc++;

                if(grid[n-1-i][m-1-j]==1)
                oc++;

                if(oc==1 || oc==3)
                reqflip+=1;

                else if(oc==2)

                reqflip+=2;
            }
        }
        int mmp=0;
        int eop=0;
            if(m%2==1)
            {
                int l=0,r=n-1;
                while(l<r)
                {
                    if(grid[l][m/2]!=grid[r][m/2])
                        mmp++;
                    else if(grid[l][m/2]==1)
                    {
                        eop++;
                    }

                    l++;
                    r--;
                }
            }
            if(n%2==1)
            {
                int l=0,r=m-1;
                while(l<r)
                {
                    if(grid[n/2][l]!=grid[n/2][r])
                        mmp++;
                    else if(grid[n/2][l]==1)
                    {
                        eop++;
                    }
                    l++;
                    r--;
                }
            }
            reqflip+=mmp;
            int totone=2*eop;
            if(n%2==1 && m%2==1)
            {
                if(grid[n/2][m/2]==1)
                {
                    totone++;
                }
            }
            int rem=totone%4;
            if(rem==0)
            return reqflip;

            if(rem==1)
            {
                return reqflip+1;
            }
            if(rem==2)
            {
                if(mmp>0)
                {
                    return reqflip;
                }
                else
                {
                    return reqflip+2;
                }
            }
            if(rem==3)
            {
                if(mmp>0)
                {
                    return reqflip+1;
                }
                else
                {
                    return reqflip+2+1;
                }
            }
            return reqflip;
    }
};
