class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int m=a.size();
        int n=a[0].size();
        int ans=0;
        auto add=[](int &c0,int &c1,int x){
            if(x==0)
                ++c0;
            else
                ++c1;
        };
        for(int i=0,j=m-1;i<j;++i,--j)
            for(int u=0,v=n-1;u<v;++u,--v){
                int c0=0;
                int c1=0;
                add(c0,c1,a[i][u]);
                add(c0,c1,a[i][v]);
                add(c0,c1,a[j][u]);
                add(c0,c1,a[j][v]);
                ans+=min(c0,c1);
            }
        int cnt=0;
        int c0=0;
        int c1=0;
        if(m%2==1){
            for(int i=0,j=n-1;i<j;++i,--j){
                int x=a[m/2][i];
                int y=a[m/2][j];
                if(x==y){
                    if(x==0)
                        c0+=2;
                    else
                        c1+=2;
                }else{
                    ++cnt;
                }
            }
        }
        if(n%2==1){
            for(int i=0,j=m-1;i<j;++i,--j){
                int x=a[i][n/2];
                int y=a[j][n/2];
                if(x==y){
                    if(x==0)
                        c0+=2;
                    else
                        c1+=2;
                }else{
                    ++cnt;
                }
            }
        }
        if(cnt){
            ans+=cnt;
        }else{
            if(c1%4!=0)
                ans+=2;
        }
        if(n%2==1&&m%2==1)
            ans+=a[m/2][n/2];
        return ans;
    }
};
