class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int i=0,ii=n-1,ans=0;

        while(i<ii){
            int j=0,jj=m-1;
            while(j<jj){
                int one=0,zero=0;

                if(grid[i][j]==1)one++;else zero++;
                if(grid[ii][j]==1)one++;else zero++;
                if(grid[i][jj]==1)one++;else zero++;
                if(grid[ii][jj]==1)one++;else zero++;

                ans+=min(one,zero);

                j++;jj--;
            }
            i++;ii--;
        }

        int flipped=0,cnt=0;
        if(m%2==1){
            int i=0,j=n-1;
            while(i<j){
                if(grid[i][m/2]!=grid[j][m/2]){flipped++;}
                else{cnt+=grid[i][m/2];cnt+=grid[j][m/2];}
                i++;j--;
            }
        }

        if(n%2==1){
            int i=0,j=m-1;
            while(i<j){
                if(grid[n/2][i]!=grid[n/2][j]){flipped++;}
                else{cnt+=grid[n/2][i];cnt+=grid[n/2][j];}
                i++;j--;
            }
        }

        if(m%2==1 && n%2==1 && grid[n/2][m/2]==1)ans++;
        ans+=flipped;

        cnt=(cnt+(2*flipped))%4;
        if(cnt==2 && flipped<1)ans+=2;

        return ans;
    }
};
