class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int flips = 0, ones = 0;


    for (int i = 0; i < m/2; i++) {
        for (int j = 0; j < n/2; j++) {
            vector<int> temp = { grid[i][j], grid[i][n - 1 - j], grid[m - 1 - i][j], grid[m - 1 - i][n - 1 - j] };

            int cnt = 0;
            for (int val : temp) 
                cnt += val;

            flips += min(cnt, 4-cnt);
        }
    }

        int extraflips =  0, cnt2 = 0, cnt1 = 0;

    if(m%2  ){
        int i = m/2 ;
        int j = 0 , k = n-1;
        while(j<k){
            if(grid[i][k] == 1 && grid[i][j] == 1){
                cnt2 += 2;
            }
            else if(grid[i][k] ^ grid[i][j]){
                cnt1++;
            }
            j++, k--;

        }
        if(j == k && grid[i][j])
            flips++, grid[i][j] = 0;
    }


    if(n%2  ){
        int j = n/2 ;
        int i = 0 , k = m-1;
        while(i<k){
            if( grid[i][j] ^ grid[k][j] )   
                cnt1++;
            else if( grid[i][j] & grid[k][j] )
                cnt2 += 2;
            i++,k--;
        }
        if(i == k && grid[i][j])
            flips++, grid[i][j] = 0;
    }

        extraflips += cnt1;
        if(cnt2%4 != 0)
        {
            if(cnt1>0)
                cnt1--;
            else
                extraflips+=2;
        }




    return flips+extraflips;

    }
};
