class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();

        int x=n/2;
        int y=m/2;

        int ans=0;

        for(int i=0;i<x;i++)
        {
            for(int j=0;j<y;j++)
            {
                int x1=i;
                int y1=j;

                int x2=n-1-i;
                int y2=j;

                int x3=i;
                int y3=m-1-j;

                int x4=n-1-i;
                int y4=m-1-j;

                int ct=0;

                if(grid[x1][y1]==1)
                ct++;

                if(grid[x2][y2]==1)
                ct++;

                if(grid[x3][y3]==1)
                ct++;

                if(grid[x4][y4]==1)
                ct++;

                int q1=ct;
                int q2=4-ct;

                ans=ans+min(q1,q2);
            }
        }

        if(n%2!=0 && m%2!=0)
        {
            if(grid[x][y]==1)
            ans++;

            int full=0;
            int half=0;

            int pt1=0;
            int pt2=m-1;

            while(pt2>pt1)
            {
                if(grid[x][pt1]!=grid[x][pt2])
                {
                    half++;
                }
                if(grid[x][pt1]==grid[x][pt2] && grid[x][pt1]==1)
                {
                    full++;
                }
                pt1++;
                pt2--;
            }


            int pt3=0;
            int pt4=n-1;

            while(pt4>pt3)
            {
                if(grid[pt4][y]!=grid[pt3][y])
                {
                    half++;
                }
                if(grid[pt4][y]==grid[pt3][y] && grid[pt3][y]==1)
                {
                    full++;
                }

                pt4--;
                pt3++;
            }

            if(full%2==0)
            {
                ans=ans+half;
            }
            else
            {
                if(half>0)
                ans=ans+half;
                else
                ans=ans+2;
            }
        }
        else if(n%2!=0)
        {
            int half=0;
            int full=0;

            int pt1=0;
            int pt2=m-1;

            while(pt2>pt1)
            {
                if(grid[x][pt1]!=grid[x][pt2])
                {
                    half++;
                }
                if(grid[x][pt1]==grid[x][pt2] && grid[x][pt1]==1)
                {
                    full++;
                }
                pt1++;
                pt2--;
            }

            if(full%2==0)
            {
                ans=ans+half;
            }
            else
            {
                if(half>0)
                ans=ans+half;
                else
                ans=ans+2;
            }
        }
        else if(m%2!=0)
        {
            int half=0;
            int full=0;

            int pt3=0;
            int pt4=n-1;

            while(pt4>pt3)
            {
                if(grid[pt4][y]!=grid[pt3][y])
                {
                    half++;
                }
                if(grid[pt4][y]==grid[pt3][y] && grid[pt3][y]==1)
                {
                    full++;
                }

                pt4--;
                pt3++;
            }

            if(full%2==0)
            {
                ans=ans+half;
            }
            else
            {
                if(half>0)
                ans=ans+half;
                else
                ans=ans+2;
            }
        }

        return ans;
    }
};
