class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int ans=0;
        int cnt_1=0;
        int var_1=0;
        for (int i = 0; i < ceil(n / 2.0); i++) {
            for (int j = 0; j < ceil(m / 2.0); j++) {
                int cnt[2] = {0};
                bool row = (j==(m-j-1));
                bool col = (i==(n-i-1));

                if(row && col){
                    ans+=grid[i][j];
                    continue;
                }

                if(row){
                    cnt[grid[i][j]]++;
                    cnt[grid[n-i-1][j]]++;
                }

                else if(col){
                    cnt[grid[i][j]]++;
                    cnt[grid[i][m-j-1]]++;
                }

                else{
                    cnt[grid[i][j]]++;
                    cnt[grid[n-i-1][j]]++;
                    cnt[grid[i][m-j-1]]++;
                    cnt[grid[n-i-1][m-j-1]]++;
                }

                if(cnt[1]>=cnt[0]){
                    if(cnt[1]==1 && cnt[0]==1){
                        var_1=2;
                    }
                    cnt_1+=(cnt[1]+cnt[0]);
                    ans+=cnt[0];
                }
                else{
                    ans+=cnt[1];
                }
            }
        }

        if(cnt_1%4==2 && var_1==0){
            ans+=2;
        }

        return ans;
    }
};
