class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        cm = m // 2
        cn = n // 2
        ans = 0
        for i in range(cm):
            for j in range(cn):
                cnt1 = 0
                cnt0 = 0
                m1 = grid[i][j]
                m2 = grid[i][n - 1 - j]
                m3 = grid[m - 1 - i][j]
                m4 = grid[m - 1 - i][n - 1 - j]

                for add in [m1, m2, m3, m4]:
                    if add == 1:
                        cnt1 += 1
                    else:
                        cnt0 += 1
                ans += min(cnt1, cnt0)
        # print(ans)
        # print(m, n, cm, cn)
        addcnt1 = 0
        addans = 0
        if m % 2 != 0:
            for j in range(cn):
                if grid[cm][j] != grid[cm][n - 1 - j]:
                    addans += 1
                else:
                    addcnt1 += grid[cm][j] * 2
        if n % 2 != 0:
            for i in range(cm):
                if grid[i][cn] != grid[m - 1 - i][cn]:
                    addans += 1
                else:
                    addcnt1 += grid[i][cn] * 2

        if n % 2 != 0 and m % 2 != 0:
            ans += grid[cm][cn]
        # print(ans, addans, addcnt1)
        if addcnt1 % 4 != 0 and addans == 0:
                return ans + 2
        else:
            return ans + addans
