class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        res = 0
        for i in range(m//2):
            for j in range(n//2):
                ones = grid[i][j] + grid[i][n-j-1] + grid[m-i-1][j] + grid[m-i-1][n-j-1]
                if ones==2: res+=2
                elif ones%4==0:pass
                else: res+=1
        if m%2==1 and n%2==1 and grid[m//2][n//2]==1: res+=1
        ones = 0
        ones_non = 0
        if m%2==1:
            for j in range(n//2):
                if grid[m//2][j]==grid[m//2][n-j-1] and grid[m//2][n-j-1]==1:
                    ones += 2
                elif grid[m//2][j]==1 or grid[m//2][n-j-1]==1:
                    ones_non += 1
        if n%2==1:
            for i in range(m//2):
                if grid[i][n//2]==grid[m-i-1][n//2] and grid[m-i-1][n//2]==1:
                    ones += 2
                elif grid[i][n//2]==1 or grid[m-i-1][n//2]==1:
                    ones_non += 1
        if ones%4==2 and ones_non==0: res+=2
        return res+ones_non
