class Solution {
     public int minFlips(int[][] grid) {
        int n = grid.length, m = grid[0].length;
        int ans = 0, shuangyi = 0, yi = 0;
        for (int i = 0; i < n / 2 + ((n % 2 == 1) ? 1 : 0); i++) {
            for (int j = 0; j < m / 2 + ((m % 2 == 1) ? 1 : 0); j++) {
                boolean flag1 = (i == n - i - 1), flag2 = (j == m - j - 1);
                if (!flag1 && !flag2) {
                    int one = 0, zero = 0;
                    if (grid[i][j] == 1) {
                        one++;
                    } else {
                        zero++;
                    }
                    if (grid[i][m - j - 1] == 1) {
                        one++;
                    } else {
                        zero++;
                    }
                    if (grid[n - i - 1][j] == 1) {
                        one++;
                    } else {
                        zero++;
                    }
                    if (grid[n - i - 1][m - j - 1] == 1) {
                        one++;
                    } else {
                        zero++;
                    }
                    ans += Math.min(one, zero);
                }
                if (flag1 && flag2) {
                    ans += grid[i][j];
                }
                if (flag1 && !flag2) {
                    if (grid[i][j] == grid[i][m - j - 1]) {
                        if (grid[i][m - j - 1] == 1) {
                            shuangyi += 2;
                        }
                    } else {
                        ans++;
                        yi++;
                    }
                }
                if (!flag1 && flag2) {
                    if (grid[i][j] == grid[n-i-1][j]) {
                        if (grid[i][j] == 1) {
                            shuangyi += 2;
                        }
                    } else {
                        ans++;
                        yi++;
                    }
                }
            }
        }
        if (shuangyi % 4 == 0) {
            return ans;
        } else if (yi > 0) {
            return ans;
        } else {
            return ans + 2;
        }
    }
}
