class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int allCountOne = 0, ans=0, delOne=0;
        for(int i=0;i<(m/2+m%2);++i){
            for(int j=0;j<(n/2+n%2);++j){
                int count = grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                // cout<<count<<endl;
                if(count==3||count==1){
                    ans+=1;
                    continue;
                }
                if(count == 2){
                    if(i!=m-1-i&&j!=n-1-j){
                        ans+=2;
                    }else{
                        ans+=1;
                        delOne+=2;
                    }
                    continue;
                }
                if(count==4){
                    if(i==m-1-i&&j==n-1-j){
                        ans+=1;
                    }else if(i==m-1-i||j==n-1-j){
                        allCountOne+=2;
                    }
                }
            }
        }
        if(delOne<allCountOne%4){
            ans+=allCountOne%4;
        }
        return ans;

    }
};
