class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();  
        int m=grid[0].size();
       int tot=m*n;
        int ans=0;
      if(tot<4){
         for(int i=0;i<n;i++){
             for(int j=0;j<m;j++){
                 if(grid[i][j]==1){
                     ans++;
                 }
             }
         }
          return ans;
      }
        else{
        int st=0;
            int en=n-1;
            int zz=0,oz=0,oo=0;
            while(st<=en){
                if(st==en){
                     int w1=0;
                int w2=m-1;
                    while(w1<=w2){
                        if(w1==w2){
                            int z=grid[st][w1];
                            if(z==1){
                                ans++;
                            }

                        }
                        else{
                            int z=grid[st][w1]+grid[en][w2];
                        if(z==0){
                            zz++;
                        }
                        else if(z==1){
                            oz++;
                        }
                        else{
                            oo++;
                        }
                        }
                        w1++;
                        w2--;
                    }
                }
                else{
                int w1=0;
                int w2=m-1;
                while(w1<=w2){
                    if(w1==w2){
                        int z=grid[st][w1]+grid[en][w2];
                        if(z==0){
                            zz++;
                        }
                        else if(z==1){
                            oz++;
                        }
                        else{
                            oo++;
                        }
                        w1++;
                        w2--;
                    }
                    else{
                        int z=grid[st][w1]+grid[st][w2]+grid[en][w1]+grid[en][w2];
                        if(z==1||z==3){
                            ans++;
                        }
                        else if(z==2){
                            ans+=2;
                        }
                        w1++;
                        w2--;
                    }
                }
                }
                st++;
                en--;
            }

            int sum=oo*2;
            if(sum%4==0){
                    ans+=oz;
            }
            else{
                if(oz>=1){
                    ans+=oz;
                }
                else if(oo>=1){
                    ans+=2;
                    ans+=oz;
                }
                else{
                    ans+=2;
                    ans+=oz;
                }
            }
           // cout<<oo<<" "<<zz<<" "<<oz<<endl;
            return ans;
        }
    }
};
