class Solution {
    public int minFlips(int[][] grid) {
        int rows = grid.length;
        int cols = grid[0].length;
        int ans = 0;
        int start = 0;
        int end = cols-1;
        while(start<end){
            int s = 0;
            int e = rows-1;
            while(s<e){
                int one = grid[s][start]+grid[e][start]+grid[s][end]+grid[e][end];
                ans += Math.min(one, 4-one);
                s++;
                e--;
            }
            start++;
            end--;
        }
        int extra = 0;
        int changed = 0;
        if(cols%2!=0){
            start = 0;
            end = rows-1;
            int col = cols/2;
            while(start<end){
                if(grid[start][col]!=grid[end][col]) changed++;
                else if(grid[start][col]==1) extra+=2;
                start++;
                end--;
            }
        }
        if(rows%2!=0){
             start = 0;
             end = cols-1;
            int row = rows/2;
            while(start<end){
                if(grid[row][start]!=grid[row][end]) changed++;
                else if(grid[row][start]==1) extra+=2;
                start++;
                end--;
            }
        }
        if(rows%2!=0 && cols%2!=0){
            int row = rows/2;
            int col = cols/2;
            if(grid[row][col]==1) extra++;
        }
        extra = extra%4;
        if(changed>0 && extra>1) extra-=2;
        return ans+changed+extra;

    }
}
