class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        int two=0;
        int one=0;
        int tw=0;

        for(int i=0;i<(m+1)/2;i++){
            for(int j=0;j<(n+1)/2;j++){

                int o=0;
                int z=0;
                int t=1;

                if(grid[i][j]==0)z++;
                else o++;

                if(i!=m-i-1 ){

                    if( grid[m-i-1][j]==0)z++;
                    else o++;

                    t++;

                }


                if(j!=n-j-1){
                    if(  grid[i][n-j-1]==0)z++;
                    else o++;
                    t++;
                }

                if((i!=m-i-1 && j!=n-j-1)){

                    if(  grid[m-i-1][n-j-1]==0)z++;
                    else o++;
                    t++;

                }


                // cout<<z<<" "<<o<<" "<<t<<endl;

                if(t==2 && o==2)two++;
                if(t==2 && o==1 && z==1)tw++;
                if(t==1 && o==1)one++;

                ans+=min(t-o,t-z);


            }
        }

        // cout<<one<<" |"<<two<<" "<<endl;

        // return ans+

        return ans+2*(max(0,two%2-tw)%2)+1*(one%4);

        if(two%2==0){
            return ans+1*(one%4);
        }


        return ans+2*((two+tw)%2)+1*(one%4);

    }
};
