class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for(int i = 0; i < n/2; i++)
        {
            for(int j = 0; j < m/2; j++)
            {
                int one = 0;
                if(grid[i][j]) one++;
                if(grid[i][m - j - 1]) one++;
                if(grid[n - i - 1][m - j - 1]) one++;
                if(grid[n - i - 1][j]) one++;
                if(one == 2) ans += 2;
                else if(one == 1 || one == 3) ans++;
            }
        }
        int flag1 = 0;
        int um1 = 0;
        if(n&1)
        {

            int mo = 0;
            for(int i = 0; i < m/2; i++)
            {
                if(grid[n/2][i] != grid[n/2][m - i - 1])
                {
                    ans++;
                    um1++;
                }
                else if(grid[n/2][i] == 1) mo++;
            }
            if(mo & 1)
            {
                flag1++;
            }
        }

        int flag2 = 0;
        int um2 = 0;
        if(m&1)
        {
            int mo = 0;
            for(int i = 0; i < n/2; i++)
            {
                if(grid[i][m/2] != grid[n - i - 1][m/2])
                {
                    ans++;
                    um2++;
                }
                else if(grid[i][m/2] == 1) mo++; 
            }
            if(mo & 1)
            {
                flag2++;
            }
        }
        if(flag1 != flag2)
        {
            if(um1 + um2) ans+=0;
            else ans += 2;
        }
        if(n % 2 == 1 && m % 2 == 1 && grid[n/2][m/2] == 1) ans++;

        return ans;

    }
};
