class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int m=grid.size(),n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int total1=0;
                if(grid[i][j]==1)total1++;
                if(grid[i][n-j-1]==1)total1++;
                if(grid[m-i-1][j]==1)total1++;
                if(grid[m-i-1][n-j-1]==1)total1++;
                if(total1<=2){
                    ans=ans+total1;
                }
                else{
                    ans=ans+4-total1;
                }


            }
        }

        int canx=0,cany=0,x1=0,y1=0;
        if(m%2==1){
            int i=m/2;
            for(int j=0;j<n/2;j++){
                if(grid[i][j]!=grid[i][n-j-1]){
                    canx++;
                }
                if(grid[i][j]==1){
                    x1++;
                }
                if(grid[i][n-j-1]==1){
                    x1++;
                }
            }
        }
        if(n%2==1){
            int j=n/2;
            for(int i=0;i<m/2;i++){
                if(grid[m-i-1][j]!=grid[i][j]){
                    cany++;
                }
                if(grid[i][j]==1){
                    y1++;
                }
                if(grid[m-i-1][j]==1){
                    y1++;
                }
            }
        }
        int canxy=canx+cany,xy1=x1+y1;
        ans+=canxy;
        xy1=xy1%4;
        for(int i=0;i<canxy;i++){
            if(xy1<2){
                xy1--;
            }
            else{
                xy1++;
            }
            xy1=(xy1+4)%4;
        }
        ans+=xy1;
        if(m%2==1&&n%2==1){
            if(grid[m/2][n/2]==1){
                ans++;
            }
        }
        return ans;
    }
};
