class Solution:
    def minFlips(self, g: List[List[int]]) -> int:
        def find(x):
            t=x
            while fa[x]!=x:
                x=fa[x]
            while t!=x:
                fa[t],t=x,fa[t]
            return x
        m,n=len(g),len(g[0])
        fa=list(range(m*n))
        sz=[1]*(m*n)
        cnt=[0]*(m*n)
        for i in range(m):
            for j in range(n):
                cnt[i*n+j]+=g[i][j]
        #print(cnt)
        for i in range(m):
            for j in range(n):
                nj=n-j-1
                f1=find(i*n+j)
                f2=find(i*n+nj)
                if f1!=f2:
                    fa[f2]=f1
                    sz[f1]+=sz[f2]
                    cnt[f1]+=cnt[f2]

        for j in range(n):
            for i in range(m):
                ni=m-i-1
                f1=find(i*n+j)
                f2=find(ni*n+j)
                if f1!=f2:
                    fa[f2]=f1
                    sz[f1]+=sz[f2]
                    cnt[f1]+=cnt[f2]
        res=0
        dp=[0,inf,inf,inf]
        for i in range(m):
            for j in range(n):
                fd=find(i*n+j)
                if fd==i*n+j:
                    #print(i,j,sz[fd],cnt[fd])
                    f=[inf]*4
                    for x in range(4):
                        f[(x+0)%4]=min(f[(x+0)%4],dp[x]+cnt[fd])
                        f[(x+sz[fd])%4]=min(f[(x+sz[fd])%4],dp[x]+sz[fd]-cnt[fd])
                    dp=f
        return dp[0]
