class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for (int i = 0; i < n / 2; i ++) {
            for (int j = 0; j < m / 2; j ++) {
                int cnt0 = 0, cnt1 = 0;
                if (grid[i][j] == 0) cnt0 ++;
                else cnt1 ++;
                if (grid[n - 1 - i][j] == 0) cnt0 ++;
                else cnt1 ++;
                if (grid[i][m - 1 - j] == 0) cnt0 ++;
                else cnt1 ++;
                if (grid[n - 1 - i][m - 1 - j] == 0) cnt0 ++;
                else cnt1 ++;

                if (cnt0 == 1 || cnt0 == 3) ans += 1;
                else if (cnt0 == 2) ans += 2;
            }
        }
        int num1 = 0, c1 = 0;
        if (n % 2) {
            for (int i = 0; i < m / 2; i ++) {
                if (grid[n / 2][i] != grid[n / 2][m - 1 - i]) c1 ++;
                else {
                    if (grid[n / 2][i] == 1) num1 += 2;
                }
            }
        }
        if (m % 2) {
            for (int i = 0; i < n / 2; i ++) {
                if (grid[i][m / 2] != grid[n - 1 - i][m / 2]) c1 ++;
                else {
                    if (grid[i][m / 2] == 1) num1 += 2;
                }
            }
        }
        if (num1 % 4 == 0) {
            ans += c1;
        }
        else if (num1 % 4 == 2) {
            if (c1) ans += c1;
            else ans += 2;
        }
        if (n % 2 && m % 2 && grid[n / 2][m / 2]) ans ++;
        return ans;
    }
};
