class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        if(n*m<4){
            int ans = 0;
            for(int i=0;i<n;i++)for(int j=0;j<m;j++)if(grid[i][j])ans++;
            return ans;
        }
        for(int i=0;i<n/2;i++){

            for(int j=0;j<m/2;j++){

                int one = 0;
                int z = 0;
                set<pair<int,int>> s;
                s.insert({i,j});
                s.insert({i,m-j-1});
                s.insert({n-i-1,j});
                s.insert({n-1-i,m-1-j});
                for(auto p:s){

                    if(grid[p.first][p.second])one++;
                }
                z = 4-one;
                ans += min(one,z);
            }
        }
        //cout<<ans<<endl;
        int ok = 0;
        int o = 0;
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]){ok++;o+=2;}
                else if(grid[i][m/2])o+=2;
            }
        }
        if(n%2){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1]){ok++;o+=2;}
                else if(grid[n/2][j])o+=2;
            }      
        }

        if(n%2 && m%2)if(grid[n/2][m/2])o++;
        int temp = o%4;
        if(temp==0){
            return ans+ok;
        }
        else if(temp==1){

            return ans+ok+1;
        }
        else if(temp==2){

            if(ok>0)return ok+ans;
            else
                return ans+2;
        }
        else{

            if(ok>0)return ans+ok+1;
            return ans+ok+3;
        }
        return ans;
    }
};
