class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n = len(grid)
        m = len(grid[0])

        vis = [[0 for i in range(m)] for i in range(n)]


        def f(i,j):

            x = [i,n-1-i]
            y = [j,m-1-j]
            ss = set()
            for i in x:
                for j in y:
                    ss.add((i,j))
            return ss

        four = 0
        changed_two = 0
        rem_two = 0
        one = 0
        ans = 0
        d = defaultdict(int)
        for i in range(n):
            for j in range(m):
                if vis[i][j]:
                    continue
                s = f(i,j)
                cnt = 0
                for xx,yy in s:
                    cnt += grid[xx][yy]
                    vis[xx][yy] = 1

                if len(s) == 2:
                    d[cnt] += 1

                elif len(s) == 4:
                    ans += min(cnt,4-cnt)
                    four += 1
                    one += 4
                else:
                    if cnt == 1:
                        ans += 1

        d[2] %= 2
        print(d)

        if d[2]:
            if d[1]:
                ans += 1
                d[1] -= 1
            else:
                ans += 2
        ans += d[1]
        return ans
