class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int ans=0,c1=0,n=grid.size(),m=grid[0].size(),c2=0;
        vector<vector<bool>>vis(n,vector<bool>(m,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(!vis[i][j]){
                    vis[i][j]=1;
                    vis[n-1-i][j]=1;
                    vis[i][m-1-j]=1;
                    vis[n-1-i][m-1-j]=1;
                    if(i==n-1-i && j==m-1-j) {
                    if(grid[i][j]==1) ans++;
                    }
                    else if(i==n-1-i){
                        int y=grid[i][j]+grid[i][m-1-j];
                        if(y==1){
                            ans++;
                            c2++;
                        }
                        else if(y==2) c1+=2;
                    }
                    else if(j==m-1-j){
                        int y=grid[n-1-i][j]+grid[i][j];
                        if(y==1){
                            ans++;
                            c2++;
                        }
                        else if(y==2) c1+=2;
                    }
                    else{
                int y=grid[i][j]+grid[n-1-i][j]+grid[i][m-1-j]+grid[n-1-i][m-1-j];
                if(y==1){
                    ans++;
                }
                else if(y==2){
                    ans+=2;
                }
                else if(y==3) {
                    ans+=1;
                }
                }
            }
        }
        }
        if(c1%4!=0 && c2==0) ans+=2; 
        return ans;
    }
};
