class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int lr = 0, rr = m - 1;
        int lc = 0, rc = n - 1;
        int res = 0, t11 = 0, t21 = 0;
        while(lr <= rr) {
            lc = 0, rc = n - 1;
            while(lc <= rc) {
                // cout << lr << " " << rr << " " << lc << " " << rc << endl;
                if(lr == rr) {
                    if(lc == rc) {
                        if(grid[lr][lc]) res++;
                        // return res;
                    } else {
                        if(grid[lr][lc] != grid[lr][rc]) t11++;
                        else if(grid[lr][lc]) t21++;
                    }
                } else {
                    if(lc == rc) {
                        if(grid[lr][lc] != grid[rr][lc]) t11++;
                        else if(grid[lr][lc]) t21++;
                    } else {
                        int cnt0 = 0, cnt1 = 0;
                        if(grid[lr][lc]) cnt1++; else cnt0++;
                        if(grid[lr][rc]) cnt1++; else cnt0++;
                        if(grid[rr][lc]) cnt1++; else cnt0++;
                        if(grid[rr][rc]) cnt1++; else cnt0++;
                        if(cnt1 == 1 || cnt1 == 3) res += 1;
                        else if(cnt1 == 2) res += 2;
                    }
                }
                // cout << res << endl;
                lc++; rc--;
            }
            lr++; rr--;
        }
        t21 %= 2;
        if(t21 && t11) t21--;
        return res + t21 * 2 + t11;
    }
};
