class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int res = 0;
        for(int i = 0; i < m / 2; i++) {
            for(int j = 0; j < n / 2; j++) {
                int temp = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][n - 1 - j];
                res += Math.min(temp, 4 - temp);
            }
        }
        int sa0 = 0, sa1 = 0, nsa = 0;
        if(m % 2 == 1) {
            for(int i = 0; i < n / 2; i++) {
                if(grid[m / 2][i] != grid[m / 2][n - 1 - i]) nsa++;
                else if(grid[m / 2][i] == 0) sa0++;
                else sa1++;
            }
        }
        if(n % 2 == 1) {
            for(int i = 0; i < m / 2; i++) {
                if(grid[i][n / 2] != grid[m - 1 - i][n / 2]) nsa++;
                else if(grid[i][n / 2] == 0) sa0++;
                else sa1++;
            }
        }
        if(m % 2 == 1 && n % 2 == 1 && grid[m / 2][n / 2] == 1) res++;
        res += nsa;
        if(sa1 % 2 == 1 && nsa == 0) res += 2;
        return res;
    }
}
