class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ans = 0
        for i in range(len(grid) // 2):
            for j in range(len(grid[0]) // 2):
                tmp = [0, 0]
                for x, y in [(i, j), (-i - 1, j), (-i - 1, -j - 1), (i, -j - 1)]:
                    tmp[grid[x][y]] += 1
                ans += min(tmp)
        tmp, s = 0, 0
        if len(grid) % 2:
            for j in range(len(grid[0]) // 2):
                if grid[len(grid) // 2][j] != grid[len(grid) // 2][-j - 1]:
                    tmp += 1
                s += grid[len(grid) // 2][j] + grid[len(grid) // 2][-j - 1]
        if len(grid[0]) % 2:
            for i in range(len(grid) // 2):
                if grid[i][len(grid[0]) // 2] != grid[-i - 1][len(grid[0]) // 2]:
                    tmp += 1
                s += grid[i][len(grid[0]) // 2] + grid[-i - 1][len(grid[0]) // 2]

        if tmp != 0:
            ans += tmp
        elif s % 4:
            ans += 2

        if len(grid) % 2 and len(grid[0]) % 2:
            ans += grid[len(grid) // 2][len(grid[0]) // 2]

        return ans
