class Solution {
public:
    int count(vector<vector<int>>& grid, int n, int m){
        int c=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int t = grid[i][j] + grid[i][m-j-1] + grid[n-i-1][j] + grid[n-i-1][m-j-1];
                c += min(t,4-t);
            }
        } 
        return c;
    }
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans=0, cnt=0, t=0;
        if(n%2){
            int k = n/2;
            for(int i=0;i<m/2;i++){
                if(grid[k][i]) cnt++;
                if(grid[k][m-i-1]) cnt++;
                if(grid[k][i] != grid[k][m-i-1]) t++;
            }
        }
        if(m%2){
            int k = m/2;
            for(int i=0;i<n/2;i++){
                if(grid[i][k]) cnt++;
                if(grid[n-i-1][k]) cnt++;
                if(grid[i][k] != grid[n-i-1][k]) t++;
            }
        }
        if((m%2) && (n%2) && (grid[n/2][m/2])) ans++; 
        ans += count(grid,n,m);
        if(n%2==0 && m%2==0) return ans;
        if(t==0 && (cnt%4==2)) ans += 2;
        else ans += t;
        return ans; 
    }
};
