#define sz(x) (int)x.size()

class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans {}, n {sz(grid)}, m {sz(grid[0])};
        for (int i = 0; i < n/2; ++i)
            for (int j = 0; j < m/2; ++j){
                int ones=grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j];
                ans+=min(ones,4-ones);
            }
        int rep {}, swaps {};
        if (n&1) {
            int i=n/2;
            for (int j = 0; j < m/2; ++j){
                if (grid[i][j]!=grid[i][m-1-j]) ++swaps;
                else if (grid[i][j]==1) ++rep;
            }
        }
        if (m&1) {
            int j=m/2;
            for (int i = 0; i < n/2; ++i) {
                if (grid[i][j]!=grid[n-1-i][j]) ++swaps;
                else if (grid[i][j]==1) ++rep;
            }
        }
        if ((rep&1)&&swaps==0) swaps+=2;
        if ((n&1)&&(m&1)&&grid[n/2][m/2]) ++ans;
        return ans+swaps;
    }
};
