class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        auto g = grid;
        int ans = 0;
        for(int i = 0;i < m/2;i++){
            for(int j = 0;j < n/2;j++){
                int i2 = i, j2 = n - 1 - j;
                int i3 = m - 1 - i, j3 = j;
                int i4 = m - 1 - i, j4 = n - 1 - j;
                int x = g[i][j] + g[i2][j2] + g[i3][j3] + g[i4][j4];
                if(x <= 2){
                    ans += x;
                }else{
                    ans += 4 - x;
                }
            }
        }
        int cnt = 0, s = 0;
        if(m%2){
            for(int i = 0;i < n/2;i++){
                if(g[m/2][i] != g[m/2][n-1-i]) s++;
                else if(g[m/2][i] == 1){
                    cnt += 2;
                }
            }
        }
        if(n%2){
            for(int i = 0;i < m/2;i++){
                if(g[i][n/2] != g[m-1-i][n/2]) s++;
                else if(g[i][n/2] == 1){
                    cnt += 2;
                }
            }
        }
        if(m%2 && n%2 && g[m/2][n/2] == 1) ans++;
        ans += s;
        if(2*s < 4 - cnt%4){
            ans += cnt%4;
        }
        return ans;
    }
};
