#define pii pair<int,int>
class Solution {
public:
    pair<int,int> f(vector<int>&v){
        int i=0, j = v.size()-1;
        int c = 0;
        while(i<=j){
            if(v[i]!=v[j]) {
               c++;
            }
            i++;
            j--;
        }
        int ans = 0;
        for(int x:v) if(x==1) ans++;
        return {c,ans};
    }
    int minFlips(vector<vector<int>>& v) {
        int n = v.size() , m = v[0].size();
        int ans = 0;
        for(int i= 0;i<n/2;i++){

            for(int j=0;j<m/2;j++){
                 map<int,int>mp;
                mp[v[i][j]]++;
                mp[v[n-1-i][j]]++;
                mp[v[i][m-1-j]]++;
                mp[v[n-1-i][m-1-j]]++;
                ans+= min(mp[0],mp[1]);
                // if(min(mp[0],mp[1])>0){
                //     cout<<i<<" "<<j <<" "<< n-1-i<<" "<<m-1-j<<" "<<v[i][j]<<" "<<v[n-1-i][j]<<" "<<v[i][m-1-j]<<" "<<v[n-1-i][m-1-j]<<endl;
                // }
            }
        }
        int mi = (n)/2 , mj = (m)/2;

        if(n%2==1 && m%2==1){
            // cout<<mi<<" "<<j<<endl;
            if(v[mi][mj]==1) ans++;
            v[mi][mj] = 0;
        }

        pii a = {0,0};
        if(n%2==1){
             a = f(v[mi]);
        }
        vector<int>v2;
        for(int i=0;i<n;i++){
            v2.push_back(v[i][mj]);
        }


        pii b = {0,0};
        if(m%2==1){
           b = f(v2);
        }
        // cout<<ans<<endl;
        int changes = a.first+ b.first;
        int ones = a.second + b.second;
        int onesNot = ones - changes;
        int onesR = (4 - onesNot%4)%4;

        // cout<<changes<<" "<<ones<<" "<<onesNot<<" "<<onesR<<endl;
        ans+= changes;
        ans+= max(0,onesR-2*changes);
        return ans;
    }
};




